BEGIN TRANSACTION;
CREATE TABLE "schema_migrations" ("version" varchar NOT NULL);
CREATE TABLE "articles" ("id" SERIAL PRIMARY KEY NOT NULL, "title" varchar, "content" text, "author" varchar, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "topic_id" integer, "is_draft" boolean);
CREATE TABLE "bootsy_images" ("id" SERIAL PRIMARY KEY NOT NULL, "image_file" varchar, "image_gallery_id" integer, "created_at" timestamp, "updated_at" timestamp);
CREATE TABLE "bootsy_image_galleries" ("id" SERIAL PRIMARY KEY NOT NULL, "bootsy_resource_id" integer, "bootsy_resource_type" varchar, "created_at" timestamp, "updated_at" timestamp);
CREATE TABLE "topics" ("id" SERIAL PRIMARY KEY NOT NULL, "name" text, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "category_id" integer);
CREATE TABLE "categories" ("id" SERIAL PRIMARY KEY NOT NULL, "name" text, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE TABLE "admins" ("id" SERIAL PRIMARY KEY NOT NULL, "first_name" varchar DEFAULT '' NOT NULL, "last_name" varchar DEFAULT '' NOT NULL, "email" varchar DEFAULT '' NOT NULL, "encrypted_password" varchar DEFAULT '' NOT NULL, "reset_password_token" varchar, "reset_password_sent_at" timestamp, "remember_created_at" timestamp, "sign_in_count" integer DEFAULT 0 NOT NULL, "current_sign_in_at" timestamp, "last_sign_in_at" timestamp, "current_sign_in_ip" varchar, "last_sign_in_ip" varchar, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_topics_on_category_id" ON "topics" ("category_id");
CREATE INDEX "index_articles_on_topic_id" ON "articles" ("topic_id");
CREATE UNIQUE INDEX "index_admins_on_email" ON "admins" ("email");
CREATE UNIQUE INDEX "index_admins_on_reset_password_token" ON "admins" ("reset_password_token");
COMMIT;
